import React, { useState } from 'react';
import './Shows.css';
import back from '../images/back.jpg';
import back2 from '../images/back2.jpg';
import back3 from '../images/back3.jpg';
import bubanj from '../images/bubanj.jpg';
import ljudi from '../images/ljudi.jpg';
import rezervisano from '../images/rezervisano.jpg';
import svirke from '../images/svirke.jpg';
const showsData = [
  {
    date: "Petak, 15.6.",
    band: "Rock Band XYZ",
    images: [bubanj,back2,back3]
  },
  {
    date: "Subota, 16.6.",
    band: "Jazz Night sa Markom",
    images: [back,ljudi,rezervisano]
  },
  {
    date: "Nedelja, 17.6.",
    band: "Akustični koncert",
    images: [
      "/images/akustic1.jpg",
      "/images/akustic2.jpg"
    ]
  }
];

const Shows = () => {
  const [selectedShowIndex, setSelectedShowIndex] = useState(null);

  return (
    <section className="shows-section">
      <h2>Najave Svirki</h2>
      <ul className="shows-list">
        {showsData.map((show, index) => (
          <li
            key={index}
            className="show-item"
            onClick={() => setSelectedShowIndex(index === selectedShowIndex ? null : index)}
          >
            <strong>{show.date}</strong> - {show.band}
            {selectedShowIndex === index && (
              <div className="gallery">
                {show.images.map((imgSrc, i) => (
                  <img
                    key={i}
                    src={imgSrc}
                    alt={`${show.band} ${i + 1}`}
                    className="gallery-image animate-scaleup"
                  />
                ))}
              </div>
            )}
          </li>
        ))}
      </ul>
    </section>
  );
};

export default Shows;
