// src/pages/App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Header from './components/Header';
import Footer from './components/Footer';

import Home from './pages/Home';
import About from './pages/About';
import Pricing from './pages/Pricing';
import Gigs from './pages/Gigs';
import Contact from './pages/Contact';

import LoginForma from './components/Login';
import RegisterForma from './components/RegisterForma';

import './App.css';


function App() {
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
        const user = localStorage.getItem('ulogovaniKorisnik');
        if (user) {
            setCurrentUser(JSON.parse(user));
        }
    }, []);

    const handleLogin = (user) => {
        localStorage.setItem('ulogovaniKorisnik', JSON.stringify(user));
        setCurrentUser(user);
    };

    const handleRegister = (user) => {
        localStorage.setItem('ulogovaniKorisnik', JSON.stringify(user));
        setCurrentUser(user);
    };

    const handleLogout = () => {
        localStorage.removeItem('ulogovaniKorisnik');
        setCurrentUser(null);
    };

    return (
        <Router>
            <div className="app-layout">
                <Header />
                <nav>
                    {currentUser ? (
                        <div>
                            <span>Dobrodošli, {currentUser.name || currentUser.ime}!</span>
                            <button onClick={handleLogout}>Odjava</button>
                        </div>
                    ) : null}
                </nav>
                <main>
                    <Routes>
                        {!currentUser ? (
                            <>
                                <Route path="/login" element={<LoginForma onLogin={handleLogin} />} />
                                <Route path="/register" element={<RegisterForma onRegister={handleRegister} />} />
                                <Route path="*" element={<Navigate to="/login" />} />
                            </>
                        ) : (
                            <>
                                <Route path="/home" element={<Home />} />
                                <Route path="/about" element={<About />} />
                                <Route path="/pricing" element={<Pricing />} />
                                <Route path="/gigs" element={<Gigs />} />
                                <Route path="/contact" element={<Contact />} />
                                <Route path="*" element={<Navigate to="/" />} />
                            </>
                        )}
                    </Routes>
                </main>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
