import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Home from './Home';
import About from './About';
import Pricing from './Pricing';
import Gigs from './Gigs';
import Login from '../components/Login';
import Register from './Register';
import Contact from './Contact';
import './App.css';
import LoginForma from '../components/Login';
import '../components/loginstil.css';
import RegisterForma from '../components/RegisterForma';



function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [showLogin, setShowLogin] = useState(true);

    const handleLogin = (user) => {
        localStorage.setItem('ulogovaniKorisnik', JSON.stringify(user));
        setCurrentUser(user);
    };

    const handleRegister = (user) => {
        localStorage.setItem('ulogovaniKorisnik', JSON.stringify(user));
        setCurrentUser(user);
    };

    const handleLogout = () => {
        localStorage.removeItem('ulogovaniKorisnik');
        setCurrentUser(null);
    };

    return (
        <div className="app">
            <nav>
                {currentUser ? (
                    <div>
                        <span>Dobrodošli, {currentUser.ime}</span>
                        <button onClick={handleLogout}>Odjava</button>
                    </div>
                ) : (
                    <div className="auth-options">
                        <button
                            onClick={() => setShowLogin(true)}
                            className={showLogin ? 'active' : ''}
                        >
                            Prijava
                        </button>
                        <button
                            onClick={() => setShowLogin(false)}
                            className={!showLogin ? 'active' : ''}
                        >
                            Registracija
                        </button>
                    </div>
                )}
            </nav>

            <main>
                {!currentUser ? (
                    showLogin ? (
                        <LoginForma onLogin={handleLogin} />
                    ) : (
                        <RegisterForma onRegister={handleRegister} />
                    )
                ) : (
                    <div>
                        <h1>Dobrodošli {currentUser.uloga}!</h1>

                    </div>
                )}
            </main>
        </div>
    );
}

function AppLayout() {
  return (
    <div className="app-layout">
      <Router>
        <Header />
        <main>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/pricing" element={<Pricing />} />
            <Route path="/gigs" element={<Gigs />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </main>
        <Footer />
      </Router>
    </div>
  );
}


export default App;
